import React from 'react';
import '../styles/Manage.css';
import {MANAGE_PATH} from "../constants/routes";
import Jumbotron from "react-bootstrap/Jumbotron";
import Row from "react-bootstrap/Row";
import Alert from "react-bootstrap/Alert";
import Dropdown from "react-bootstrap/Dropdown";
import Accordion from "react-bootstrap/Accordion";
import Card from "react-bootstrap/Card";
import Form from "react-bootstrap/Form";
import Col from "react-bootstrap/Col";
import Button from "react-bootstrap/Button";
import Spinner from "react-bootstrap/Spinner";

class Manage extends React.Component {
    constructor(props) {
        super(props);
        this.state = {
            isLoading: true,
            accountData: '',
        }
    };

    componentDidMount() {
        this.props.sendGetRequest(MANAGE_PATH)
            .then((response) => {
                this.setState({accountData: response['data']});
                this.setState({isLoading: false});
            }).catch((error) => {
                console.log(error.response);
        })
    }

    pushChange = (statName) => {

    };

    changeableStatRender = (statName, className) => {
        console.log(statName);
        const statNameToBeRendered = statName.replace('_', ' ');
        return (
            <Accordion defaultActiveKey="0" className={className}>
                <Card>
                    <Accordion.Toggle as={Card.Header} eventKey="0">
                        <h4 className="text-capitalize">{statNameToBeRendered}: {this.state.accountData[statName]['value']}</h4>
                    </Accordion.Toggle>
                    <Accordion.Collapse eventKey="0">
                        <Card.Body>
                            <Form>
                                <Form.Group as={Row} controlId="formCharacterNameChange" className="mb-0">
                                    <Form.Label column>
                                        New {statNameToBeRendered}
                                    </Form.Label>
                                    <Col md={{span: 7}}>
                                        <Form.Control type="username" placeholder="Character name" />
                                    </Col>
                                    <Col md={{span: 2}}>
                                        <Button block type="submit" onClick={() => this.pushChange(statName)}>
                                            Change
                                        </Button>
                                    </Col>
                                </Form.Group>
                            </Form>
                        </Card.Body>
                    </Accordion.Collapse>
                </Card>
            </Accordion>
        )
    };

    render() {
        const rowClassNameControl = "ml-auto mr-auto mt-2 mb-2 w-75";
        if (!this.state.isLoading) {
            return (
                <Jumbotron className="text-center">
                    <Alert variant="dark" className={rowClassNameControl}>
                        <h2>Username: {this.state.accountData['name']['value']}</h2>
                    </Alert>
                    {this.changeableStatRender('character_name', rowClassNameControl)}
                </Jumbotron>
            );
        } else {
            return (
                <Spinner animation="primary" />
            )
        }
    }
}

export default Manage;
