import React, {Component} from 'react';
import Accordion from "react-bootstrap/Accordion";
import Card from "react-bootstrap/Card";
import "../../styles/FightResult.css";

class FightResult extends Component {
    constructor(props) {
        super(props);
        this.state = {
            roundsReady: false,
        }
    }


    renderRounds = () => {
        const roundsDescription = this.props.fightCourse['rounds'];
        const rounds = roundsDescription.map((roundData, roundNumber) => {
            const round = 'Round #' + (roundNumber + 1);
            let attacker_misc;
            let defender_misc;

            if (roundData['attacker_critical'] && roundData['defender_dodge']) {
                attacker_misc = '(critical, dodged).';
            } else if (roundData['attacker_critical']) {
                attacker_misc = '(critical).';
            } else if (roundData['defender_dodge']) {
                attacker_misc = '(dodged).';
            } else {
                attacker_misc = '.';
            }

            if (roundData['defender_critical'] && roundData['attacker_dodge']) {
                defender_misc = '(critical, dodged).';
            } else if (roundData['defender_critical']) {
                defender_misc = '(critical).';
            } else if (roundData['attacker_dodge']) {
                defender_misc = '(dodged).';
            } else {
                defender_misc = '.';
            }

            const attacker = 'Attacker attacks. The attack has done ' + roundData['attacker_damage'] + ' damage ' + attacker_misc;
            const defender = "Defender's turn. His attack does " + roundData['defender_damage'] + ' damage ' + defender_misc;
            const attacker_health = 'Attacker has ' + roundData['attacker_health'] + ' health left.';
            const defender_health = 'Defender has ' + roundData['attacker_health'] + ' health left.';
            return (
                <React.Fragment key={'fragment' + roundNumber}>
                    <h3 key={round} className="text-center">{round}</h3>
                        <p className="ml-6" key={'attacker' + roundNumber}>{attacker}</p>
                        <p className="mr-6" key={'defender'  + roundNumber}>{defender}</p>
                </React.Fragment>
            );
        });
        return (
            <ol key={"rounds-list"} className="list-group-item-light list-unstyled">
                {rounds}
            </ol>
        )
    };

    render() {
        if (this.props.show) {
            return (
                <React.Fragment>
                    <Accordion defaultActiveKey="1" className="fight-result-accordion">
                        <Card>
                            <Accordion.Toggle as={Card.Header} eventKey="0">
                                Winner:
                                <strong>
                                    {
                                        this.props.fightCourse['winner']
                                        ? " You!"
                                        : " Not you"
                                    }
                                </strong>
                            </Accordion.Toggle>
                            <Accordion.Collapse eventKey="0">
                                <Card.Body>
                                    {this.renderRounds()}
                                </Card.Body>
                            </Accordion.Collapse>
                        </Card>
                    </Accordion>
                </React.Fragment>
            );
        }
        return <p></p>
    }
}

export default FightResult;
