import React from 'react';
import '../styles/Arena.css';
import Row from 'react-bootstrap/Row';
import Col from 'react-bootstrap/Col';
import {ARENA_FIGHT_PATH, ARENA_PATH} from "../constants/routes";
import Spinner from "react-bootstrap/Spinner";
import CharacterEquipment from "./SubComponents/CharacterEquipment";
import CharacterStats from "./SubComponents/CharacterStats";
import Button from "react-bootstrap/Button";
import Container from "react-bootstrap/Container";

class Arena extends React.Component {
    constructor(props) {
        super(props);
        this.state = {
            enemyData: {},
            isLoading: true,
        }
    }
    componentDidMount() {
        this.props.sendGetRequest(ARENA_PATH)
            .then((response) => {
                this.setState({enemyData: response['data']['enemy']});
                this.setState({isLoading: false});
            }).catch((error) => {
                console.log(error.response);
        })
    }

    render() {
        console.log(this.state.enemyData);
        if (!this.state.isLoading) {
            let status;
            return (
                <React.Fragment>
                    <Container className="justify-content-center">
                        <CharacterEquipment
                            characterData={this.state.enemyData}
                        />
                    </Container>
                    <Row className="justify-content-center">
                        <Col md="auto">
                            <CharacterStats
                                characterData={this.state.enemyData}
                                sendPostRequest={this.props.sendPostRequest}
                                manageStats={false}
                            />
                        </Col>
                    </Row>
                    <Row className="justify-content-center row">
                        <Col md={"auto"}>
                            <Button
                                className="text-center"
                                variant="warning"
                                size="lg"
                                onClick={() => {
                                    this.props.sendPostRequest(ARENA_FIGHT_PATH, {defender_id: this.state.enemyData['id']})
                                        .then((response) => {
                                            console.log(response['data']);
                                            return (
                                                <Button>XDXDXDXDXD</Button>
                                            );
                                        }).catch((error) => {
                                            status = error.response['message'];
                                    })
                                }}
                            >
                                Attack
                            </Button>
                        </Col>
                    </Row>
                    <Row className="justify-content-center">
                        <Col md={"auto"}>
                            <p>{status}</p>
                        </Col>
                    </Row>
                </React.Fragment>
            );
        } else {
            return (
                <Spinner
                    className='loading-spinner'
                    animation="border"
                    variant="primary"
                />
            )
        }
    }
}

export default Arena;
