import React from 'react';
import axios from 'axios';
import { Route, Switch, Redirect } from 'react-router-dom';
import '../styles/App.css';

import Login from "./NotLoggedIn/Login";

import Character from "./Character";
import Arena from "./Arena";
import Expedition from "./Expedition";

import ArmorTrader from "./ArmorTrader";
import WeaponTrader from "./WeaponTrader";
import Healer from "./Healer";

import Manage from "./Manage";
import Logout from "./Logout";
import {CHARACTER_PATH} from "../constants/routes";

class App extends React.Component {
    constructor(props) {
        super(props);
        this.state = {
            isAuthenticated: false,
        }
    }

    setIsAuthenticated = (newAuthenticationStatus) => {
        this.setState({isAuthenticated: newAuthenticationStatus});
    };

    checkIfAuthenticated = () => {
        axios.get(CHARACTER_PATH, {
            headers: {
                'Authorization': 'Bearer ' + window.localStorage.getItem('access_token')
            }
        }).then((response) =>
            {
                this.setIsAuthenticated(true);
            })
            .catch((error) => {
                this.setIsAuthenticated(false);
            })
    };

    sendGetRequest = (path) => {
        return new Promise((resolve, reject) => {
            axios.get(path, {
                headers: {
                    'Authorization': 'Bearer ' + window.localStorage.getItem('access_token')
                }
            }).then((response) => {
                resolve(response);
            }).catch((error) => {
                if (error.response.status === 401) {
                    this.setIsAuthenticated(false);
                }
                reject(error);
            })
        })
    };

    componentDidMount() {
        this.sendGetRequest(CHARACTER_PATH)
            .then((response) => {
                console.log(response);
            }).catch((error) => {
                console.log(error.response);
        });
    };

    render() {

        return (
            <Switch>
                <Route exact path="/">
                    {
                        this.state.isAuthenticated
                            ? <Redirect to="/login" />
                            : <Redirect to="/character" />
                    }
                </Route>
                <Route exact path="/login">
                    {
                        !this.state.isAuthenticated
                            ? <Login setIsAuthenticated={this.setIsAuthenticated} />
                            : <Redirect to="/character" />
                    }
                </Route>

                <Route path="/character">
                    {
                        this.state.isAuthenticated
                            ? <Character />
                            : <Redirect to="/login" />
                    }
                </Route>

                <Route path="/arena">
                    {
                        this.state.isAuthenticated
                            ? <Arena />
                            : <Redirect to="/login" />
                    }
                </Route>

                <Route path="/expedition">
                    {
                        this.state.isAuthenticated
                            ? <Expedition />
                            : <Redirect to="/login" />
                    }
                </Route>

                <Route path="/npc/armor">
                    {
                        this.state.isAuthenticated
                            ? <ArmorTrader />
                            : <Redirect to="/login" />
                    }
                </Route>

                <Route path="/npc/weapon">
                    {
                        this.state.isAuthenticated
                            ? <WeaponTrader />
                            : <Redirect to="/login" />
                    }
                </Route>

                <Route path="/npc/healer">
                    {
                        this.state.isAuthenticated
                            ? <Healer />
                            : <Redirect to="/login" />
                    }
                </Route>

                <Route path="/manage">
                    {
                        this.state.isAuthenticated
                            ? <Manage />
                            : <Redirect to="/login" />
                    }
                </Route>

                <Route path="/logout">
                    {
                        this.state.isAuthenticated
                            ? <Logout />
                            : <Redirect to="/login" />
                    }
                </Route>
            </Switch>
        )
    }
}

export default App;
