import React from 'react';
import '../../styles/Healer.css';
import {HEALER_PATH} from "../../constants/routes";
import {Image} from "react-bootstrap";
import Jumbotron from "react-bootstrap/Jumbotron";
import ProgressBar from "react-bootstrap/ProgressBar";
import Badge from "react-bootstrap/Badge";
import Button from "react-bootstrap/Button";
import Spinner from "react-bootstrap/Spinner";

class Healer extends React.Component {
    constructor(props) {
        super(props);
        this.state = {
            npcData: {},
            buttonState: true,
            isLoading: true,
            charGold: 0,
        }
    }

    componentDidMount() {
        this.props.sendGetRequest(HEALER_PATH)
            .then((response) => {
                console.log(response);
                this.setState({npcData: response['data']});
                this.setState({isLoading: false});
                this.setState({charGold: response['data']['char_gold']});
            }).catch((error) => {
                console.log(error.response);
        })
    }

    render() {
        if (!this.state.isLoading) {
            let health = this.state.npcData['health'];
            const maxHealth = this.state.npcData['max_health'];
            const healthBadgeColor = health > (maxHealth / 2) ? 'success' : 'warning';
            const price = this.state.npcData['price'];
            return (
                <Jumbotron>
                    <div className="text-center">
                        <Image
                            src={this.state.npcData['img_path']}
                            height={400}
                            width={280}
                            alt="healer image"
                        />
                        <span className="mt-3 align-text-bottom form-control">
                       {this.state.npcData['text']}
                    </span>
                        <
                            ProgressBar
                            className="bg-info mb-0 mt-1"
                            animated
                            variant={"success"}
                            now={health}
                            max={maxHealth}
                        />
                        <p>
                            <Badge className="text-center mt-0" variant={healthBadgeColor}>
                                {health} / {maxHealth}
                            </Badge>
                        </p>
                        <Button
                            // className={}
                            size="lg"
                            active={this.state.buttonState}
                            disabled={!this.state.buttonState}
                            onClick={() => {
                                this.setState({charGold: this.state.charGold - (price * 100)});
                                this.setState({buttonState: this.state.charGold > price});
                                console.log(this.state.charGold);
                                console.log(this.state.buttonState);
                            }}
                        >
                            Heal for {price} gold
                        </Button>
                    </div>
                </Jumbotron>
            );
        } else {
            return <Spinner animation="primary" />
        }

}
}

export default Healer;
