import React from 'react';
import axios from 'axios';

import './App.css';

import Login from "./components/NotLoggedIn/Login";
// import NotLoggedInMenu from "./components/NotLoggedIn/NotLoggedInMenu";

import Menu from './components/Menu';
import Character from "./components/Character";
import Button from "react-bootstrap/Button";

class App extends React.Component {
    constructor(props) {
        super(props);
        this.state = {
            apiBaseUrl: 'http://127.0.0.1:5000/api',
            storedTokens: false,
        }

    }

    automaticLogin() {
        axios.get(this.state.apiBaseUrl + '/validate', {
            headers: {
                Authorization: 'Bearer ' + window.localStorage.getItem('access_token'),
            }
        }).then(function(response) {
            if (response['data']['msg'] === 'Missing Authorization Header') {}
            else {
                console.log('validated');
                this.setState({
                    storedTokens: true,
                })
            }
        });
    }

    render() {
        this.automaticLogin();
        console.log(this.state);
        return (
            <div>
                {this.state.storedTokens ? <Character /> : <Login apiBaseUrl={this.state.apiBaseUrl} />}

            {/*<Login apiBaseUrl={this.state.apiBaseUrl}/>*/}

            {/*<Menu />*/}
            {/*<Character />*/}
            </div>
        )
    }
}

export default App;
