import React from 'react';
import '../styles/Character.css';
import Jumbotron from "react-bootstrap/Jumbotron";
import Container from 'react-bootstrap/Container';
import Row from 'react-bootstrap/Row';
import Col from 'react-bootstrap/Col';
import {
    ADD_STATS_PATH,
    CHARACTER_PATH,
    CHARACTER_STAT_DISPLAY_NAME,
    INCREASING_STATS,
    ITEM_STAT_DISPLAY_NAME,
    PLACEHOLDER_IMAGES
} from "../constants/routes";
import Image from "react-bootstrap/Image";
import Spinner from "react-bootstrap/Spinner";
import OverlayTrigger from "react-bootstrap/OverlayTrigger";
import Tooltip from "react-bootstrap/Tooltip";
import Button from "react-bootstrap/Button";

class Character extends React.Component {
    constructor(props) {
        super(props);
        this.state = {
            characterData: {},
            isLoading: true,
            freeStats: 0,
            }
        }

    componentDidMount() {
    this.props.sendGetRequest(CHARACTER_PATH)
            .then((response) => {
                this.setState({characterData: response['data']});
                this.setState({freeStats: response['data']['free_stats']});
                this.setState({isLoading: false});
            }).catch((error) => {
                console.log(error.response);
        });
    };

    getItemImagePath = (gameItemType) => {
        return this.state.characterData['items'][gameItemType]['image_path'];
    };

    getItemDescription = (gameItemType) => {
        const item = this.state.characterData['items'][gameItemType];
        const stats = Object.keys(ITEM_STAT_DISPLAY_NAME).map(statName => {
            return (
                <p className="item-description-row">{ITEM_STAT_DISPLAY_NAME[statName]}: {item[statName]}</p>
            );
        });
        return (
            <Tooltip id="tooltip-bottom">
                <strong>{item['name']}</strong> <br/>
                <small>{item['slot']}</small> <br/>
                {stats}
            </Tooltip>
        );
    };

    renderImageWithDescription = (gameItemType, gameItemHeight) => {
        if (gameItemType in this.state.characterData['items']) {
            return (
                <OverlayTrigger
                    trigger="hover"
                    placement="bottom"
                    overlay={this.getItemDescription(gameItemType)}
                >
                    <Image
                        src={this.getItemImagePath(gameItemType)}
                        height={gameItemHeight}
                        alt={"character " + gameItemType}
                    />
                </OverlayTrigger>
            )
        } else {
            return (
                <Image
                    src={PLACEHOLDER_IMAGES[gameItemType]}
                    height={gameItemHeight}
                    alt={"character " + gameItemType}
                />
            )
        }
    };

    renderCharacterStats = () => {
        let char = this.state.characterData;
        const stats = Object.keys(CHARACTER_STAT_DISPLAY_NAME).map(statName => {
            const renderButton = INCREASING_STATS[statName];
            return (
                <li key={statName}>
                    <strong>{CHARACTER_STAT_DISPLAY_NAME[statName]}</strong>: {char[statName]}
                    {renderButton ?
                        <Button
                            className="stats-button btn-circle"
                            variant="primary"
                            size="sm"
                            active={this.state.freeStats !== 0}
                            disabled={this.state.freeStats === 0}
                            onClick={() => {
                                this.props.sendPostRequest(ADD_STATS_PATH, {'stat': statName})
                                    .then((response) => {
                                        char[response['data']['increased']] += response['data']['increased_by'];
                                        this.setState({characterData: char});
                                        this.setState({freeStats: this.state.freeStats - 1});
                                    });
                            }}
                        >+</Button>
                        : null
                    }
                </li>
            )
        });
        return (
            <ul className="list-unstyled">
                {stats}
            </ul>
        )
    };

    render() {
        console.log(this.state.characterData);

        if (!this.state.isLoading) {
            const amuletOffset = 7;
            return (
                <Jumbotron className="character-view">
                    <Container>
                        <Row className="justify-content-center">
                            <Col md={0}>
                                <h3>{this.state.characterData['name']}</h3>
                            </Col>
                        </Row>

                        <Row className="justify-content-center">
                            <Col md={0}>
                                {this.renderImageWithDescription('helmet', 75)}
                            </Col>
                        </Row>
                        <Row className="justify-content-center">
                            <Col
                            lg={{offset: amuletOffset}} md={{offset: amuletOffset}} sm={{offset: amuletOffset}}
                            xl={{offset: amuletOffset}} xs={{offset: amuletOffset}}>
                                {this.renderImageWithDescription('amulet', 35)}
                            </Col>
                        </Row>

                        <Row className="justify-content-center">
                            <Col md="auto">
                                {this.renderImageWithDescription('weapon', 150)}
                            </Col>

                            <Col md="auto">
                                {this.renderImageWithDescription('armor', 155)}
                            </Col>

                            <Col md="auto">
                                {this.renderImageWithDescription('shield', 150)}
                            </Col>
                        </Row>
                        <Row>
                            <Col className="stats-viewer">
                                {this.renderCharacterStats()}
                            </Col>
                        </Row>
                    </Container>
                </Jumbotron>
            );
        }
        else {
            return (
                <Spinner
                    className='loading-spinner'
                    animation="border"
                    variant="primary"
                />
            )
        }
    }
}

export default Character;
