import React, {Component} from 'react';
import axios from 'axios';

import Form from "react-bootstrap/Form";
import Button from "react-bootstrap/Button";
import "./Login.css";
import NotLoggedInMenu from "./NotLoggedInMenu";


class Login extends Component {
    constructor(props) {
        super(props);
        this.state = {
            serverMessage: '',
        }

    }
    handleLogin = (event) => {
        event.preventDefault();
        let username = event.target.username.value;
        let password = event.target.password.value;
        axios.post(this.props.apiBaseUrl + '/login', {
            username: username,
            password: password,
        }).then(function (response) {
            const data = response['data'];
            window.localStorage.setItem('access_token', data['access_token']);
            window.localStorage.setItem('refresh_token', data['refresh_token']);
        })

    };
    render() {
        return (
            <div>
                <NotLoggedInMenu activeSite={"#"}/>
                <div className="initial-form">
                    <Form onSubmit={this.handleLogin}>
                        <Form.Group controlId="formBasicEmail">
                            <Form.Label size="lg">Username</Form.Label>
                            <Form.Control name="username" type="username" placeholder="Enter username" size="lg" />
                        </Form.Group>
                        <Form.Group controlId="formBasicPassword">
                            <Form.Label>Password</Form.Label>
                            <Form.Control name="password" type="password" placeholder="Password" size="lg" />
                        </Form.Group>
                        <Button variant="primary" type="submit" size="lg" block>
                            Log me In
                        </Button>
                        <Button variant="primary" type="submit" size="lg" block>
                            Sign Up
                        </Button>
                    </Form>
                </div>
            </div>
        );
    }
}

export default Login;
