import React from 'react';
import '../styles/Expedition.css';
import Container from 'react-bootstrap/Container';
import Row from 'react-bootstrap/Row';
import Col from 'react-bootstrap/Col';
import {EXPEDITION_PATH} from "../constants/routes";
import Spinner from "react-bootstrap/Spinner";
import {Image} from "react-bootstrap";

class Expedition extends React.Component {
    constructor(props) {
        super(props);
        this.state = {
            enemyData: {},
            isLoading: true,
        }
    }

    componentDidMount() {
        this.props.sendGetRequest(EXPEDITION_PATH)
            .then((response) => {
                this.setState({enemyData: response['data']['enemy']});
                this.setState({isLoading: false});
            }).catch((error) => {
                console.log(error.response);
        })
    }

    render() {
        console.log(this.state.enemyData);
        if (!this.state.isLoading) {
            return (
                <React.Fragment>
                    <Row className="justify-content-center">
                        <Image
                            src={this.state.enemyData['image_path']}
                            alt="monster image"
                        />
                    </Row>
                </React.Fragment>
            );
        } else {
            return <Spinner  animation="primary"/>
        }
    }
}

export default Expedition;
