import React, {Component} from 'react';
import Spinner from "react-bootstrap/Spinner";
import Accordion from "react-bootstrap/Accordion";
import Card from "react-bootstrap/Card";
import "../../styles/FightResult.css";

class FightResult extends Component {
    constructor(props) {
        super(props);
        this.state = {
            roundsReady: false,
        }
    }


    renderRounds = () => {
        const roundsDescription = this.props.fightCourse['rounds'];
        const rounds = roundsDescription.map((roundData, roundNumber) => {
            const round = 'Round #' + (roundNumber + 1);
            console.log(round);
            console.log(roundData);
            console.log(roundNumber);
                let attacker_misc;
            let defender_misc;

            if (roundData['attacker_critical'] && roundData['defender_dodge']) {
                attacker_misc = '(critical, dodged).';
            } else if (roundData['attacker_critical']) {
                attacker_misc = '(critical).';
            } else if (roundData['defender_dodge']) {
                attacker_misc = '(dodged).';
            } else {
                attacker_misc = '.';
            }

            if (roundData['defender_critical'] && roundData['attacker_dodge']) {
                defender_misc = '(critical, dodged).';
            } else if (roundData['defender_critical']) {
                defender_misc = '(critical).';
            } else if (roundData['attacker_dodge']) {
                defender_misc = '(dodged).';
            } else {
                defender_misc = '.';
            }

            const attacker = 'Attacker did ' + roundData['attacker_damage']
                + ' damage' + attacker_misc;
            const defender = 'Defender did ' + <strong>roundData['defender_damage']</strong>
                + ' damage' + defender_misc;
            return (
                <React.Fragment>
                    <h3 key={round}>{round}</h3>
                    <p key={round + '_attacker'}>{attacker}</p>
                    <p key={round + '_defender'}>{defender}</p>
                </React.Fragment>
            );
        });
        return (
            <ol>
                {rounds}
            </ol>
        )
    };

    render() {
        if (this.props.show) {
            return (
                <React.Fragment>
                    <Accordion defaultActiveKey="0" className="fight-result-accordion">
                        <Card>
                            <Accordion.Toggle as={Card.Header} eventKey="0">
                                Winner:
                                <strong>
                                    {
                                        this.props.fightCourse['winner']
                                        ? " You!"
                                        : " Not you"
                                    }
                                </strong>
                            </Accordion.Toggle>
                            <Accordion.Collapse eventKey="0">
                                <Card.Body>
                                    {this.renderRounds()}
                                </Card.Body>
                            </Accordion.Collapse>
                        </Card>
                    </Accordion>
                </React.Fragment>
            );
        }
        return <p></p>
    }
}

export default FightResult;
