import React from 'react';
import axios from 'axios';
import { Route, Switch, Redirect } from 'react-router-dom';
import '../styles/App.css';

import Spinner from "react-bootstrap/Spinner";

import Login from "./Authentication/Login";

import Character from "./Character";
import Arena from "./Arena";
import Expedition from "./Expedition";

import ArmorTrader from "./NPC/ArmorTrader";
import WeaponTrader from "./NPC/WeaponTrader";
import Healer from "./NPC/Healer";

import Manage from "./Manage";
import Logout from "./Logout";

import Menu from "./Menu";
import NotLoggedInMenu from "./Authentication/NotLoggedInMenu";

import { REFRESH_PATH } from "../constants/routes";

class App extends React.Component {
    constructor(props) {
        super(props);
        this.state = {
            isAuthenticated: false,
            isLoading: true,
        }
    }

    componentDidMount() {
        this.sendGetRequest(REFRESH_PATH)
            .then((response) => {
                this.setIsAuthenticated(true);
                this.setState({isLoading: false});
            }).catch((error) => {
                this.setIsAuthenticated(false);
                this.setState({isLoading: false});
                console.log(error.response);
        });
    };

    setIsAuthenticated = (newAuthenticationStatus) => {
        this.setState({isAuthenticated: newAuthenticationStatus});
    };

    sendGetRequest = (path) => {
        return new Promise((resolve, reject) => {
            axios.get(path, {
                headers: {
                    'Authorization': 'Bearer ' + window.localStorage.getItem('access_token'),
                    'refresh_token': window.localStorage.getItem('refresh_token')
                }
            }).then((response) => {
                resolve(response);
            }).catch((error) => {
                if (error.response.status === 401) {
                    this.setIsAuthenticated(false);
                }
                reject(error);
            })
        })
    };

    sendPostRequest = (path, data) => {
        return new Promise((resolve, reject) => {
            axios.post(path, data,{
                headers: {
                    'Authorization': 'Bearer ' + window.localStorage.getItem('access_token'),
                    'refresh_token': window.localStorage.getItem('refresh_token'),
                }
            }).then((response) => {
                resolve(response);
            }).catch((error) => {
                if (error.response.status === 401) {
                    this.setIsAuthenticated(false);
                }
                reject(error);
            })
        })
    };

    render() {

        if (this.state.isLoading) {
            return (
                <Spinner
                    className='loading-spinner'
                    animation="border"
                    variant="primary"
                />
            );
        }

        return (
            <React.Fragment>
                {this.state.isAuthenticated
                ?
                    <Menu className="top-side-menu"
                        sendPostRequest={this.sendPostRequest}
                        setIsAuthenticated={this.setIsAuthenticated}
                    />
                : <NotLoggedInMenu className="top-side-menu" />
                }
                <Switch>
                    <Route exact path="/">
                        {
                            this.state.isAuthenticated
                                ? <Redirect to="/login" />
                                : <Redirect to="/character" />
                        }
                    </Route>
                    <Route exact path="/login">
                        {
                            !this.state.isAuthenticated
                                ? <Login setIsAuthenticated={this.setIsAuthenticated} />
                                : <Redirect to="/character" />
                        }
                    </Route>

                    <Route path="/character">
                        {
                            this.state.isAuthenticated
                                ?
                                <Character
                                    sendGetRequest={this.sendGetRequest}
                                    sendPostRequest={this.sendPostRequest}
                                />
                                : <Redirect to="/login" />
                        }
                    </Route>

                    <Route path="/arena">
                        {
                            this.state.isAuthenticated
                                ?
                                <Arena
                                    sendGetRequest={this.sendGetRequest}
                                    sendPostRequest={this.sendPostRequest}
                                />
                                : <Redirect to="/login" />
                        }
                    </Route>

                    <Route path="/expedition">
                        {
                            this.state.isAuthenticated
                                ?
                                <Expedition
                                    sendGetRequest={this.sendGetRequest}
                                    sendPostRequest={this.sendPostRequest}
                                />
                                : <Redirect to="/login" />
                        }
                    </Route>

                    <Route path="/npc/armor">
                        {
                            this.state.isAuthenticated
                                ?
                                <ArmorTrader
                                    sendGetRequest={this.sendGetRequest}
                                    sendPostRequest={this.sendPostRequest}
                                />
                                : <Redirect to="/login" />
                        }
                    </Route>

                    <Route path="/npc/weapon">
                        {
                            this.state.isAuthenticated
                                ?
                                <WeaponTrader
                                    sendGetRequest={this.sendGetRequest}
                                    sendPostRequest={this.sendPostRequest}
                                />
                                : <Redirect to="/login" />
                        }
                    </Route>

                    <Route path="/npc/healer">
                        {
                            this.state.isAuthenticated
                                ? <
                                Healer
                                    sendGetRequest={this.sendGetRequest}
                                    sendPostRequest={this.sendPostRequest}
                                />
                                : <Redirect to="/login" />
                        }
                    </Route>

                    <Route path="/manage">
                        {
                            this.state.isAuthenticated
                                ?
                                <Manage
                                    sendGetRequest={this.sendGetRequest}
                                    sendPostRequest={this.sendPostRequest}
                                />
                                : <Redirect to="/login" />
                        }
                    </Route>

                    <Route path="/logout">
                        {
                            this.state.isAuthenticated
                                ?
                                <Logout
                                    sendGetRequest={this.sendGetRequest}
                                    sendPostRequest={this.sendPostRequest}
                                />
                                : <Redirect to="/login" />
                        }
                    </Route>
                </Switch>
            </React.Fragment>
        )
    }
}

export default App;
