import React from 'react';
import '../styles/Manage.css';
import {MANAGE_PATH} from "../constants/routes";
import Jumbotron from "react-bootstrap/Jumbotron";
import Row from "react-bootstrap/Row";
import Alert from "react-bootstrap/Alert";
import Dropdown from "react-bootstrap/Dropdown";
import Accordion from "react-bootstrap/Accordion";
import Card from "react-bootstrap/Card";
import Form from "react-bootstrap/Form";
import Col from "react-bootstrap/Col";
import Button from "react-bootstrap/Button";

class Manage extends React.Component {
    constructor(props) {
        super(props);
        this.state = {
            isLoading: true,
            accountData: '',
        }
    };

    componentDidMount() {
        this.props.sendGetRequest(MANAGE_PATH)
            .then((response) => {
                this.setState({accountData: response['data']});
            }).catch((error) => {
                console.log(error.response);
        })
    }

    changeableStatRender = (statName, className) => {
        console.log(statName);
        return (
            <Accordion defaultActiveKey="0" className={className}>
                <Card>
                    <Accordion.Toggle as={Card.Header} eventKey="0">
                        <h4>Character Name: {this.state.accountData['character_name']['value']}</h4>
                    </Accordion.Toggle>
                    <Accordion.Collapse eventKey="0">
                        <Card.Body>
                            <Form>
                                <Form.Group as={Row} controlId="formCharacterNameChange" className="mb-0">
                                    <Form.Label column md={4}>
                                        New Character Name
                                    </Form.Label>
                                    <Col md={7}>
                                        <Form.Control type="username" placeholder="Character name" />
                                    </Col>
                                </Form.Group>
                            </Form>
                        </Card.Body>
                    </Accordion.Collapse>
                </Card>
            </Accordion>
        )
    };

    render() {
        const rowClassNameControl = "ml-auto mr-auto mt-2 mb-2 w-75";
        return (
            <Jumbotron className="text-center">
                <Alert variant="dark" className={rowClassNameControl}>
                    <h2>Username: {this.state.accountData['name']['value']}</h2>
                </Alert>
                {this.changeableStatRender('x', rowClassNameControl)}
            </Jumbotron>
        );
    }
}

export default Manage;
