import React, {Component} from 'react';
import Spinner from "react-bootstrap/Spinner";

class FightResult extends Component {
    constructor(props) {
        super(props);
        this.state = {
            isLoading: true,
        }
    }

    render() {
        if (this.props.show) {
            if (!this.state.isLoading) {
                return (
                    <React.Fragment>
                        
                    </React.Fragment>
                );
            } else {
                return (
                    <Spinner
                        className='loading-spinner'
                        animation="border"
                        variant="primary"
                    />
                );
            }
        }
        return <p></p>
    }
}

export default FightResult;
