import React from 'react';
import axios from 'axios';
import { Route, Switch, Redirect } from 'react-router-dom';
import '../styles/App.css';

import Login from "./NotLoggedIn/Login";

import Character from "./Character";
import Arena from "./Arena";
import Expedition from "./Expedition";

import ArmorTrader from "./ArmorTrader";
import WeaponTrader from "./WeaponTrader";
import Healer from "./Healer";

import Manage from "./Manage";
import Logout from "./Logout";

class App extends React.Component {
    constructor(props) {
        super(props);
        this.state = {
            isAuthenticated: false,
        }
    }

    setIsAuthenticated = (newAuthenticationStatus) => {
        this.setState({isAuthenticated: newAuthenticationStatus});
    };

    render() {
        return (
            <Switch>
                <Route exact path="/">
                    {
                        this.state.isAuthenticated
                            ? <Redirect to="/login" />
                            : <Redirect to="/character" />
                    }
                </Route>
                <Route exact path="/login">
                    {
                        !this.state.isAuthenticated
                            ?
                                <Login
                                    setIsAuthenticated={this.setIsAuthenticated}
                                />
                            : <Redirect to="/character" />
                    }
                </Route>

                <Route path="/character">
                    {
                        this.state.isAuthenticated
                            ? <Character />
                            : <Redirect to="/login" />
                    }
                </Route>

                <Route path="/arena">
                    {
                        this.state.isAuthenticated
                            ? <Arena />
                            : <Redirect to="/login" />
                    }
                </Route>

                <Route path="/expedition">
                    {
                        this.state.isAuthenticated
                            ? <Character />
                            : <Redirect to="/login" />
                    }
                </Route>

                <Route path="/npc/armor">
                    {
                        this.state.isAuthenticated
                            ? <Character />
                            : <Redirect to="/login" />
                    }
                </Route>

                <Route path="/npc/weapon">
                    {
                        this.state.isAuthenticated
                            ? <Character />
                            : <Redirect to="/login" />
                    }
                </Route>

                <Route path="/npc/heal">
                    {
                        this.state.isAuthenticated
                            ? <Character />
                            : <Redirect to="/login" />
                    }
                </Route>

                <Route path="/manage">
                    {
                        this.state.isAuthenticated
                            ? <Manage />
                            : <Redirect to="/login" />
                    }
                </Route>

                <Route path="/npc/logout">
                    {
                        this.state.isAuthenticated
                            ? <Character />
                            : <Redirect to="/login" />
                    }
                </Route>
            </Switch>
        )
    }
}

export default App;
