import React, {Component} from 'react';
import axios from 'axios';

import { LOGIN_PATH } from "../../constants/routes";
import Form from "react-bootstrap/Form";
import Button from "react-bootstrap/Button";
import "../../styles/Login.css";
import NotLoggedInMenu from "./NotLoggedInMenu";


class Login extends Component {
    constructor(props) {
        super(props);
        this.state = {
            serverMessage: '',
        }

    }

    handleLogin = (event) => {
        event.preventDefault();
        let username = event.target.username.value;
        let password = event.target.password.value;
        axios.post(LOGIN_PATH, {
            username: username,
            password: password,
        }).then((response) => {
            const data = response['data'];
            console.log(response);
            /*
            data:
            success: false
            message: "Wrong password."
            */

            // data:
            //     success: true
            // message: "Logged in successfully."
            // access_token: "eyJ0eXAiOiJKV1QiLCJhbGciOiJIUzI1NiJ9.eyJpYXQiOjE1Nzk0MzUzNzQsIm5iZiI6MTU3OTQzNTM3NCwianRpIjoiNWQ2OTQzYWEtZGUwMC00N2NjLWI4YTItZTBmNGQ0NzQ3NDNlIiwiZXhwIjoxNTc5NDM2Mjc0LCJpZGVudGl0eSI6MywiZnJlc2giOmZhbHNlLCJ0eXBlIjoiYWNjZXNzIn0.ePGQLJAtCr7KWSMiIvBz20BZGWTfZINF_zaKrWtPzW8"
            // refresh_token: "eyJ0eXAiOiJKV1QiLCJhbGciOiJIUzI1NiJ9.eyJpYXQiOjE1Nzk0MzUzNzQsIm5iZiI6MTU3OTQzNTM3NCwianRpIjoiOThmZDRmNGQtMjZhMS00NjBiLTlmM2MtMjc3MDc0OWY1NTRmIiwiZXhwIjoxNTgyMDI3Mzc0LCJpZGVudGl0eSI6MywidHlwZSI6InJlZnJlc2gifQ.gHFrTphUKlhmUA_W3n3vGkr2xuffU5Wk4T_qSO2v86U"
            // redirect: "/character"

            window.localStorage.setItem('access_token', data['access_token']);
            window.localStorage.setItem('refresh_token', data['refresh_token']);
            this.setState({serverMessage: data['message']})
        })
            .catch((error) => {
                console.log('WYJEBAŁEM SIĘ')
                this.setState({serverMessage: 'Something went wrong. Try again later.'})
            })

    };

    render() {
        return (
            <div>
                <NotLoggedInMenu activeSite={"#"}/>
                <div className="initial-form">
                    <Form onSubmit={this.handleLogin}>
                        <Form.Group controlId="formBasicEmail">
                            <Form.Label size="lg">Username</Form.Label>
                            <Form.Control name="username" type="username" placeholder="Enter username" size="lg" />
                        </Form.Group>
                        <Form.Group controlId="formBasicPassword">
                            <Form.Label>Password</Form.Label>
                            <Form.Control name="password" type="password" placeholder="Password" size="lg" />
                        </Form.Group>
                        <Form.Text className="text-muted server-message">{this.state.serverMessage}</Form.Text>
                        <Button variant="primary" type="submit" size="lg" block>
                            Log me In
                        </Button>
                        <Button variant="primary" type="submit" size="lg" block>
                            Sign Up
                        </Button>
                    </Form>
                </div>
            </div>
        );
    }
}

export default Login;
