import React from 'react';
import '../styles/Manage.css';
import {MANAGE_PATH} from "../constants/routes";
import Jumbotron from "react-bootstrap/Jumbotron";
import Row from "react-bootstrap/Row";
import Alert from "react-bootstrap/Alert";
import Dropdown from "react-bootstrap/Dropdown";

class Manage extends React.Component {
    constructor(props) {
        super(props);
        this.state = {
            isLoading: true,
            accountData: '',
        }
    };

    componentDidMount() {
        this.props.sendGetRequest(MANAGE_PATH)
            .then((response) => {
                this.setState({accountData: response['data']});
                console.log(response);
            }).catch((error) => {
                console.log(error.response);
        })
    }

    render() {
        const alertsClassName = "ml-auto mr-auto mt-2 mb-2 w-75";
        return (
            <Jumbotron className="text-center">
                <Alert variant="dark" className={alertsClassName}>
                    <h2>Username: {this.state.accountData['name']}</h2>
                </Alert>
                <Dropdown variant="primary" className={alertsClassName} title={"Character Name: " + this.state.accountData['character_name']}>

                </Dropdown>
            </Jumbotron>
        );
    }
}

export default Manage;
