import React, {Component} from 'react';
import Spinner from "react-bootstrap/Spinner";
import Accordion from "react-bootstrap/Accordion";
import Card from "react-bootstrap/Card";
import "../../styles/FightResult.css";

class FightResult extends Component {

    renderRounds = () => {
        const roundsDescription = this.props.fightCourse['rounds'];
        const rounds = roundsDescription.map((roundNumber, roundData) => {
            const round = 'Round #' + roundNumber;
            return (
                <Accordion defaultActiveKey="0">
                    <Card>
                        <Accordion.Toggle as={Card.Header} eventKey="0">
                            
                        </Accordion.Toggle>
                    </Card>
                </Accordion>
            )
        });
    };

    render() {
        if (this.props.show) {
            return (
                <React.Fragment>
                    <Accordion defaultActiveKey="0" className="fight-result-accordion">
                        <Card>
                            <Accordion.Toggle as={Card.Header} eventKey="0">
                                Winner:
                                <strong>
                                    {
                                        this.props.fightCourse['winner']
                                        ? " You!"
                                        : " Not you"
                                    }
                                </strong>
                            </Accordion.Toggle>
                            <Accordion.Collapse eventKey="0">
                                <Card.Body>
                                    {this.renderRounds()}
                                </Card.Body>
                            </Accordion.Collapse>
                        </Card>
                    </Accordion>
                </React.Fragment>
            );
        }
        return <p></p>
    }
}

export default FightResult;
