import React from 'react';
import '../styles/Character.css';
import Jumbotron from "react-bootstrap/Jumbotron";
import Container from 'react-bootstrap/Container';
import Row from 'react-bootstrap/Row';
import Col from 'react-bootstrap/Col';
import {CHARACTER_PATH} from "../constants/routes";
import Image from "react-bootstrap/Image";
import Spinner from "react-bootstrap/Spinner";

class Character extends React.Component {
    constructor(props) {
        super(props);
        this.state = {
            characterData: {},
            isLoading: true,
        }
    }

    componentDidMount() {
        this.props.sendGetRequest(CHARACTER_PATH)
            .then((response) => {
                this.setState({characterData: response['data']});
                this.setState({isLoading: false});
            })
    }

    getItemImagePath = (gameItemType) => {
        return this.state.characterData['items'][gameItemType]['image_path'];
    };
    render() {
        console.log(this.state.characterData);
        if (!this.state.isLoading) {
            return (
                <Jumbotron>
                    <Container fluid>
                        <Row className="justify-content-md-center">
                            <Col md="auto">
                                <h3>{this.state.characterData['name']}</h3>
                            </Col>
                        </Row>

                        <Row>
                            <Col>
                                <Image
                                    className="character-helmet"
                                    src={this.getItemImagePath('helmet')}
                                    alt="character helmet"
                                />
                            </Col>
                        </Row>
                        <Row>
                            <Col>
                                <Image
                                    className="character-amulet"
                                    src={this.getItemImagePath('amulet')}
                                    alt="character amulet"
                                />
                            </Col>
                        </Row>
                        <Row>
                            <Col>
                                <Image
                                    className="character-hand"
                                    src={this.getItemImagePath('weapon')}
                                    alt="character weapon"
                                />
                            </Col>

                            <Col>
                                <Image
                                    className="character-armor"
                                    src={this.getItemImagePath('armor')}
                                    alt="character weapon"
                                />
                            </Col>

                            <Col>
                                <Image
                                    className="character-hand"
                                    src={this.getItemImagePath('shield')}
                                    alt="character weapon"
                                />
                            </Col>
                        </Row>
                    </Container>
                </Jumbotron>
            );
        }
        else {
            return (
                <Spinner
                    className='loading-spinner'
                    animation="border"
                    variant="primary"
                />
            )
        }
    }
}

export default Character;
