import React from 'react';
import axios from 'axios';
import { Route, Switch, Redirect } from 'react-router-dom';
import '../styles/App.css';

import Spinner from "react-bootstrap/Spinner";

import Login from "./NotLoggedIn/Login";

import Character from "./Character";
import Arena from "./Arena";
import Expedition from "./Expedition";

import ArmorTrader from "./ArmorTrader";
import WeaponTrader from "./WeaponTrader";
import Healer from "./Healer";

import Manage from "./Manage";
import Logout from "./Logout";
import {CHARACTER_PATH} from "../constants/routes";

export const AjaxCommunicationContext = React.createContext(null);


class App extends React.Component {
    constructor(props) {
        super(props);
        this.state = {
            isAuthenticated: false,
            isLoading: true,
        }
    }

    componentDidMount() {
        this.sendGetRequest(CHARACTER_PATH)
            .then((response) => {
                this.setIsAuthenticated(true);
                this.setState({isLoading: false});
            }).catch((error) => {
                this.setIsAuthenticated(false);
                this.setState({isLoading: false});
                console.log(error.response);
        });
    };

    setIsAuthenticated = (newAuthenticationStatus) => {
        this.setState({isAuthenticated: newAuthenticationStatus});
    };

    sendGetRequest = (path) => {
        return new Promise((resolve, reject) => {
            axios.get(path, {
                headers: {
                    'Authorization': 'Bearer ' + window.localStorage.getItem('access_token')
                }
            }).then((response) => {
                resolve(response);
            }).catch((error) => {
                if (error.response.status === 401) {
                    this.setIsAuthenticated(false);
                }
                reject(error);
            })
        })
    };

    sendPostRequest = (path, data) => {
        return new Promise((resolve, reject) => {
            axios.post(path, data,{
                headers: {
                    'Authorization': 'Bearer ' + window.localStorage.getItem('access_token')
                }
            }).then((response) => {
                resolve(response);
            }).catch((error) => {
                if (error.response.status === 401) {
                    this.setIsAuthenticated(false);
                }
                reject(error);
            })
        })
    };

    getAjaxMethodsObject = () => ({
        sendGetResponse: this.sendGetRequest,
        sendPostResponse: this.sendPostRequest
    });

    render() {

        if (this.state.isLoading) {
            return (
                <Spinner
                    className='loading-spinner'
                    animation="border"
                    variant="primary"
                />
            );
        }

        return (
            <Switch>
                <Route exact path="/">
                    {
                        this.state.isAuthenticated
                            ? <Redirect to="/login" />
                            : <Redirect to="/character" />
                    }
                </Route>
                <Route exact path="/login">
                    {
                        !this.state.isAuthenticated
                            ? <Login setIsAuthenticated={this.setIsAuthenticated} />
                            : <Redirect to="/character" />
                    }
                </Route>

                <Route path="/character">
                    {
                        this.state.isAuthenticated
                            ?
                            <AjaxCommunicationContext.Provider value={this.getAjaxMethodsObject()}>
                                <Character />
                            </AjaxCommunicationContext.Provider>
                            : <Redirect to="/login" />
                    }
                </Route>

                <Route path="/arena">
                    {
                        this.state.isAuthenticated
                            ?
                            <AjaxCommunicationContext.Provider value={this.getAjaxMethodsObject()}>
                                <Arena />
                            </AjaxCommunicationContext.Provider>
                            : <Redirect to="/login" />
                    }
                </Route>

                <Route path="/expedition">
                    {
                        this.state.isAuthenticated
                            ? <Expedition />
                            : <Redirect to="/login" />
                    }
                </Route>

                <Route path="/npc/armor">
                    {
                        this.state.isAuthenticated
                            ? <ArmorTrader />
                            : <Redirect to="/login" />
                    }
                </Route>

                <Route path="/npc/weapon">
                    {
                        this.state.isAuthenticated
                            ? <WeaponTrader />
                            : <Redirect to="/login" />
                    }
                </Route>

                <Route path="/npc/healer">
                    {
                        this.state.isAuthenticated
                            ? <Healer />
                            : <Redirect to="/login" />
                    }
                </Route>

                <Route path="/manage">
                    {
                        this.state.isAuthenticated
                            ? <Manage />
                            : <Redirect to="/login" />
                    }
                </Route>

                <Route path="/logout">
                    {
                        this.state.isAuthenticated
                            ? <Logout />
                            : <Redirect to="/login" />
                    }
                </Route>
            </Switch>
        )
    }
}

export default App;
