import React from 'react';
import '../styles/Manage.css';
import {MANAGE_PATH} from "../constants/routes";
import Jumbotron from "react-bootstrap/Jumbotron";
import Row from "react-bootstrap/Row";
import Alert from "react-bootstrap/Alert";
import Dropdown from "react-bootstrap/Dropdown";
import Accordion from "react-bootstrap/Accordion";
import Card from "react-bootstrap/Card";
import Form from "react-bootstrap/Form";

class Manage extends React.Component {
    constructor(props) {
        super(props);
        this.state = {
            isLoading: true,
            accountData: '',
        }
    };

    componentDidMount() {
        this.props.sendGetRequest(MANAGE_PATH)
            .then((response) => {
                this.setState({accountData: response['data']});
                console.log(response);
            }).catch((error) => {
                console.log(error.response);
        })
    }

    render() {
        const alertsClassName = "ml-auto mr-auto mt-2 mb-2 w-75";
        return (
            <Jumbotron className="text-center">
                <Alert variant="dark" className={alertsClassName}>
                    <h2>Username: {this.state.accountData['name']}</h2>
                </Alert>
                <Accordion defaultActiveKey="1" className={alertsClassName}>
                    <Card>
                        <Accordion.Toggle as={Card.Header} eventKey="0">
                            <h4>Character Name: {this.state.accountData['character_name']}</h4>
                        </Accordion.Toggle>
                        <Accordion.Collapse eventKey="0">
                            <Card.Body className="pt-2">
                                <Form>
                                    
                                </Form>
                            </Card.Body>
                        </Accordion.Collapse>
                    </Card>
                </Accordion>
            </Jumbotron>
        );
    }
}

export default Manage;
