import React, {Component} from 'react';
import {RANKING_PATH} from "../constants/routes";
import Spinner from "react-bootstrap/Spinner";
import Jumbotron from "react-bootstrap/Jumbotron";
import Table from "react-bootstrap/Table";
import Button from "react-bootstrap/Button";

class Ranking extends Component {
    constructor(props) {
        super(props);
        this.state = {
            isLoading: true,
            activePage: 1,
            maxPages: -1,
            tableHeaders: [],
            characters: {},
            price: 0,
            sortBy: 'experience',
            order: 'DESC',
            errorStatus: '',
            tableRowKey: '',
        }
    }

    componentDidMount() {
        const params = {
            'sort_by': this.state.sortBy,
            'page': this.state.activePage,
            'price': this.state.price,
            'order': this.state.order,
        };

        this.props.sendGetRequestWithData(RANKING_PATH, params)
            .then((response) => {
                this.setState({tableHeaders: response['data']['headers']});
                this.setState({characters: response['data']['characters']});
                console.log(response);
                this.setState({isLoading: false})
            }).catch((error) => {
                console.log("ERROR");
                console.log(error.response);
                this.setState({errorStatus: error.response['data']['message']});
        })
    }

    renderTableHead = () => {
        return this.state.tableHeaders.map(header => {
            return <th key={header} className="p-0"><Button block className="m-0 bg-transparent text-black-50 border-0">{header}</Button></th>
        });
    };

    renderTableRows = () => {
        const characters = this.state.characters;
        return Object.keys(this.state.characters).map(characterNumber => {
            const characterData = characters[characterNumber];
            const stats = Object.keys(characterData).map(characterStat => {
                return <td key={characterData['name'] + '-' + characterStat}>{characterData[characterStat]}</td>
            });
            return (
                <tr key={characterNumber}>
                    <td>{characterNumber}</td>
                    {stats}
                </tr>
            )
        })
    };

    render() {
        if (!this.state.isLoading) {
            return (
                <Table bordered hover striped className="w-75 ml-auto mr-auto text-center">
                    <thead>
                        <tr>
                            {this.renderTableHead()}
                        </tr>
                    </thead>
                    <tbody>
                        {this.renderTableRows()}
                    </tbody>
                </Table>
            );
        } else {
            return <Spinner animation="primary" />
        }
    }
}

export default Ranking;
