import React from 'react';
import '../../styles/Trader.css';
import Jumbotron from "react-bootstrap/Jumbotron";
import {ITEM_STAT_DISPLAY_NAME, TRADER_PATH} from "../../constants/routes";
import {Image} from "react-bootstrap";
import Spinner from "react-bootstrap/Spinner";
import Card from "react-bootstrap/Card";

class Trader extends React.Component {
    constructor(props) {
        super(props);
        this.state = {
            isLoading: true,
            itemsReady: false,
            npcName: '',
            npcImagePath: '',
            npcText: '',
            charGold: 0,
            traderItems: {},
        }
    }


    componentDidMount() {
        this.props.sendGetRequest(TRADER_PATH)
            .then((response) => {
                this.setState({npcName: response['data']['name']});
                this.setState({npcImagePath: response['data']['img_path']});
                this.setState({npcText: response['data']['text']});
                this.setState({charGold: response['data']['gold']});
                this.setState({traderItems: response['data']['items']});
                this.setState({isLoading: false});
            }).catch((error) => {
                console.log(error.response);
        })
    }

    renderItemsRow = (itemsInRow) => {
        const items = itemsInRow.map((itemData) => {
            console.log(itemData);

            const itemName = itemData['name'];
            const itemId = itemData['id'];
            return (
                <div
                    key={"item-data-for-" + itemName + "-(id " + itemId + ")"}
                    className="w-25 d-inline-block text-center"
                >
                    <span className="text-capitalize text-center">
                        <h3 className="bg-light mb-0">{itemName}</h3>
                        <p className="bg-light mt-0">{itemData['slot']}</p>
                    </span>
                </div>
            )
        });
        return items;
    };

    renderItems = () => {
        const topRow = this.state.traderItems.splice(0, 2);
        const bottomRow = this.state.traderItems.splice(3);
        console.log(topRow);
        console.log(bottomRow);
    };

    render() {
        if (!this.state.isLoading) {
            return (
                <Jumbotron>
                    <div className="text-center">
                        <Image
                            src={this.state.npcImagePath}
                            height={400}
                            width={280}
                            alt="healer image"
                        />
                        <span className="mt-3 align-text-bottom form-control">
                            <strong>{this.state.npcName}</strong>: {this.state.npcText}
                        </span>
                    </div>
                    <div>
                        {this.renderItems()}
                    </div>
                </Jumbotron>
            );
        } else {
            return (
                <Spinner animation="primary"/>
            );
        }
    }
}

export default Trader;
