import React from 'react';
import '../../styles/Healer.css';
import {HEALER_PATH} from "../../constants/routes";
import Row from "react-bootstrap/Row";
import Col from "react-bootstrap/Col";
import {Image} from "react-bootstrap";
import Jumbotron from "react-bootstrap/Jumbotron";
import ProgressBar from "react-bootstrap/ProgressBar";
import Badge from "react-bootstrap/Badge";

class Healer extends React.Component {
    constructor(props) {
        super(props);
        this.state = {
            npcData: {},
        }
    }

    componentDidMount() {
        this.props.sendGetRequest(HEALER_PATH)
            .then((response) => {
                console.log(response);
                this.setState({npcData: response['data']});
            }).catch((error) => {
                console.log(error.response);
        })
    }

    render() {
        let health = this.state.npcData['health'];
        const maxHealth = this.state.npcData['max_health'];
        const healthBadgeColor = health > (maxHealth / 2) ? 'success' : 'warning';
        return (
            <Jumbotron>
                <div className="text-center">
                    <Image
                        src={this.state.npcData['img_path']}
                        height={400}
                        width={280}
                        alt="healer image"
                    />
                    <span className="mt-3 align-text-bottom form-control">
                       {this.state.npcData['text']}
                    </span>
                    <
                        ProgressBar
                        className="bg-info mb-0 mt-1"
                        animated
                        variant={"success"}
                        now={health}
                        max={maxHealth}
                    />
                    <Badge className="text-center mt-0" variant={healthBadgeColor}>
                        {health} / {maxHealth}
                    </Badge>
                </div>
            </Jumbotron>
        );
    }
}

export default Healer;
